	upSteamDate()

	/**
	 * 获取现在的时间
	 * @return {Object} - 时间对象
	 * */
	function getDate() {
		let date = new Date()
		return {
			year: date.getFullYear(),
			month: date.getMonth() + 1,
			date: date.getDate(),
			sec: date.getSeconds(),
			timeStatus: date.getTime()
		}
	}
